import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [url, setUrl] = useState('');
  const [crawledData, setCrawledData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchCrawledData();
  }, []);

  const fetchCrawledData = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/crawled-data');
      setCrawledData(response.data);
    } catch (error) {
      console.error('Error fetching crawled data:', error);
      setError('Failed to fetch crawled data');
    }
  };

  const handleCrawl = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    try {
      const response = await axios.post('http://localhost:3001/api/crawl', { url });
      setCrawledData([response.data]);
      setUrl('');
    } catch (error) {
      console.error('Error crawling URL:', error);
      setError('Failed to crawl the URL. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>GenK.vn Web Crawler</h1>
      <form onSubmit={handleCrawl}>
        <input
          type="text"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          placeholder="Enter URL to crawl (e.g., https://genk.vn/)"
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Crawling...' : 'Crawl'}
        </button>
      </form>
      {error && <p style={{color: 'red'}}>{error}</p>}
      <h2>Crawled Data:</h2>
      {crawledData.map((item, index) => (
        <div key={index}>
          <h3>Crawled URL: {item.url}</h3>
          <p>Page Title: {item.title}</p>
          <h4>Post Links: {item.postLinks ? item.postLinks.length : 0}</h4>
          {item.postLinks && item.postLinks.length > 0 ? (
            <table>
              <thead>
                <tr>
                  <th>Title</th>
                  <th>URL</th>
                </tr>
              </thead>
              <tbody>
                {item.postLinks.map((post, postIndex) => (
                  <tr key={postIndex}>
                    <td>{post.title}</td>
                    <td><a href={post.url} target="_blank" rel="noopener noreferrer">{post.url}</a></td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>No post links found.</p>
          )}
        </div>
      ))}
    </div>
  );
}

export default App;
