import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [url, setUrl] = useState('');
  const [crawledData, setCrawledData] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchCrawledData();
  }, []);

  const fetchCrawledData = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/crawled-data');
      setCrawledData(response.data);
    } catch (error) {
      console.error('Error fetching crawled data:', error);
    }
  };

  const handleCrawl = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await axios.post('http://localhost:3001/api/crawl', { url });
      await fetchCrawledData();
      setUrl('');
    } catch (error) {
      console.error('Error crawling URL:', error);
    }
    setLoading(false);
  };

  return (
    <div className="App">
      <h1>Web Crawler</h1>
      <form onSubmit={handleCrawl}>
        <input
          type="text"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          placeholder="Enter URL to crawl"
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Crawling...' : 'Crawl'}
        </button>
      </form>
      <h2>Crawled Data:</h2>
      <ul>
        {crawledData.map((item, index) => (
          <li key={index}>
            <strong>{item.url}</strong>
            <p>Title: {item.title}</p>
            <p>Links: {item.links ? item.links.length : 0}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
